import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

import {
  Button,
  Card,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Col,
} from 'reactstrap';

const Register = () => {

  const [name, setName] = useState(""); // Declare and initialize 'email'
  const [email, setEmail] = useState(""); // Declare and initialize 'applicationNo'
  const [code, setCode] = useState('');
  const navigate = useNavigate();



  const generateAlphanumericCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < 16; i++) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
  };


  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Generate a new alphanumeric code
      const newCode = generateAlphanumericCode();
      setCode(newCode);

      // Send a POST request to the backend with JSON data
      await axios.post('http://localhost:5000/api/users/register', {
        name: formData.name,
        email: formData.email,
        code: newCode,
      });

      // Handle success if needed
      console.log('Registration successful!');
    } catch (error) {
      // Handle errors
      console.error('Error submitting form:', error.message);
    }
  };

  const handleLoginButtonClick = () => {
    navigate('auth/login');
  };

  return (
    <>
      <Col lg="6" md="8">
        <Card className="bg-secondary shadow border-0">
          <CardBody className="px-lg-5 py-lg-5">
            <div className="text-center text-muted mb-4">
              <small>Submit Your Research Paper</small>
            </div>
            <Form role="form" onSubmit={handleSubmit}>
              <FormGroup>
                <InputGroup className="input-group-alternative mb-3">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-hat-3" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input name="name" placeholder="name" type="text" />
                </InputGroup>
              </FormGroup>
              <FormGroup>
                <InputGroup className="input-group-alternative mb-3">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-email-83" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input name="email" placeholder="email" type="email" autoComplete="new-email" />
                </InputGroup>
              </FormGroup>

              {/* Submit Button */}
              <div className="text-center">
                <Button className="mt-4" color="primary" type="submit">
                  Submit
                </Button>
              </div>
            </Form>

            {/* Display Alphanumeric Code */}
            {code && (
              <div className="text-center mt-3">
                <strong>Your Application Code:</strong> {code}. Keep your Application Number for future logins.
              </div>
            )}
            <div className="text-center">
              <Button className="mt-4" color="primary" type="login" onClick={() => handleLoginButtonClick()}>
                Log In
              </Button>
            </div>
          </CardBody>
        </Card>
      </Col>
    </>
  );
};

export default Register;
