import React, { useState, useEffect } from "react";
import axios from "axios";
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
// reactstrap components
import {
  //Button,
  Card,
  CardHeader,
  CardBody,
  // NavItem,
  // NavLink,
  // Nav,
  Progress,
  Table,
  Container,
  Row,
  Col,
  Button,
} from "reactstrap";

// core components
import {
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2,
} from "variables/charts.js";

import Header from "components/Headers/Header.js";

const Admin_Das = (props) => {
  const [activeNav, setActiveNav] = useState(1);
  const [chartExample1Data, setChartExample1Data] = useState("data1");
  const [recentUpdates, setRecentUpdates] = useState([]);

  const fetchRecentUpdates = async () => {
    try {
      const response = await axios.get(
        "http://192.168.1.211:5002/api/review-updates"
      );
      setRecentUpdates(response.data);
    } catch (error) {
      console.error("Error fetching recent updates:", error.message);
    }
  };

  useEffect(() => {
    fetchRecentUpdates();
  }, []);
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data("data" + index);
  };

  const handleApprove = async (code) => {
    try {
      // Find the recent update with the specified code
      const updateToApprove = recentUpdates.find((update) => update.code === code);

      // Check if the status is already "published"
      if (updateToApprove.status === 'published') {
        console.log('Already approved, cannot change status again.');
        return;
      }

      // Make an API call to update the status to "published"
      await axios.put(`http://192.168.1.211:5002/api/update-status/${code}`, {
        status: 'published',
      });

      // Refetch recent updates after the status update
      fetchRecentUpdates();
    } catch (error) {
      console.error('Error updating status:', error.message);
    }
  };

  const handleDisapprove = async (code) => {
    try {
      // Find the recent update with the specified code
      const updateToDisapprove = recentUpdates.find((update) => update.code === code);

      // Check if the status is already "published"
      if (updateToDisapprove.status === 'published') {
        console.log('Already approved, cannot change status again.');
        return;
      }

      // Make an API call to update the status to "rejected"
      await axios.put(`http://192.168.1.211:5002/api/update-status/${code}`, {
        status: 'rejected',
      });

      // Refetch recent updates after the status update
      fetchRecentUpdates();
    } catch (error) {
      console.error('Error updating status:', error.message);
    }
  };

  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
        <Row>
          <Col className="mb-5 mb-xl-0" xl="7">
            <Card className="bg-gradient-default shadow">
              <CardHeader className="bg-transparent">
                <Row className="align-items-center">
                  <div className="col">
                    <h6 className="text-uppercase text-light ls-1 mb-1">
                      Overview
                    </h6>
                    <h2 className="text-white mb-0">Papers Submitted in Various Sectors</h2>
                  </div>
                  <div className="col">
                  </div>
                </Row>
              </CardHeader>
              <CardBody>
                {/* Chart */}
                <div className="chart">
                  <Line
                    data={chartExample1[chartExample1Data]}
                    options={chartExample1.options}
                    getDatasetAtEvent={(e) => console.log(e)}
                  />
                </div>
              </CardBody>
            </Card>
          </Col>
          <Col xl="5">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <Row className="align-items-center">
                  <div className="col">
                    <h6 className="text-uppercase text-muted ls-1 mb-1">
                      Applications
                    </h6>
                    <h2 className="mb-0">Total Submissions</h2>
                  </div>
                </Row>
              </CardHeader>
              <CardBody>
                {/* Chart */}
                <div className="chart">
                  <Bar
                    data={chartExample2.data}
                    options={chartExample2.options}
                  />
                </div>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row className="mt-5">
          <Col className="mb-5 mb-xl-0" xl="8">
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">Recent Updates</h3>
                  </div>
                </Row>
              </CardHeader>
              <Table className="align-items-center table-flush" responsive>
                <thead className="thead-light">
                  <tr>
                    <th scope="col">Username</th>
                    <th scope="col">Application Code</th>
                    <th scope="col">Approve/Dis-approve</th>
                    <th scope="col">Current status</th>
                    <th scope="col">Score</th>
                  </tr>
                </thead>
                <tbody>
                  {recentUpdates.map((update) => (
                    <tr key={update.code}>
                      <td>{/* Add username from update object */}</td>
                      <td>{update.code}</td>
                      <td>
                        <Button
                          color="success"
                          className="mr-2"
                          onClick={() => handleApprove(update.code)}
                        >
                          <i className="fas fa-check" />
                        </Button>
                        <Button
                          color="danger"
                          onClick={() => handleDisapprove(update.code)}
                        >
                          <i className="fas fa-times" />
                        </Button>
                      </td>
                      <td>{update.status}</td>
                      <td>{update.score}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card>
          </Col>
          <Col xl="4">
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <div className="col">
                    <h3 className="mb-0">Live Status</h3>
                  </div>
                  <div className="col text-right">
                  </div>
                </Row>
              </CardHeader>
              <Table className="align-items-center table-flush" responsive>
                <thead className="thead-light">
                  <tr>
                    <th scope="col">Application ID</th>

                    <th scope="col">Status</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th scope="row">5679ERTP6789</th>

                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">60%</span>
                        <div>
                          <Progress
                            max="100"
                            value="60"
                            barClassName="bg-gradient-danger"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">4534ERTP7809</th>

                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">70%</span>
                        <div>
                          <Progress
                            max="100"
                            value="70"
                            barClassName="bg-gradient-success"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">3456ERTP0001</th>

                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">80%</span>
                        <div>
                          <Progress max="100" value="80" />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">5679ERTP2035</th>

                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">75%</span>
                        <div>
                          <Progress
                            max="100"
                            value="75"
                            barClassName="bg-gradient-info"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                  <tr>
                    <th scope="row">4533ERTP6789</th>

                    <td>
                      <div className="d-flex align-items-center">
                        <span className="mr-2">30%</span>
                        <div>
                          <Progress
                            max="100"
                            value="30"
                            barClassName="bg-gradient-warning"
                          />
                        </div>
                      </div>
                    </td>
                  </tr>
                </tbody>
              </Table>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Admin_Das;
