import React, { useState } from 'react';
import {
  Button,
  Card,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Col,
} from 'reactstrap';



const mongoose = require('mongoose');

const dbUrl="mongodb+srv://01012001hariom:c8cWqs7i7KlIMsTO@cluster0.joc1xyi.mongodb.net/";

const connectionParams = {
  useNewUrlParser:true,
  useUnifiedTopology:true
};
mongoose.connect(dbUrl,connectionParams).then(()=>{
  console.info("connected to the db");
}).catch((e)=>{
  console.log("Error:",e);
});


const User=require('/home/apmosys/Desktop/IEEE Authenticator/Frontend/src/models/UserModel.js'); 

async function insert(){
  await User.create({
    name:'Sandeep',
    email:'sandeep@gmail.com'
  });
}
insert();




const Register = () => {
  const [code, setCode] = useState('');

  const handleFileChange = (event) => {
    // Handle file change here if needed
  };

  const generateAlphanumericCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < 16; i++) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newCode = generateAlphanumericCode();
    setCode(newCode);
  };

  return (
    <>
      <Col lg="6" md="8">
        <Card className="bg-secondary shadow border-0">
          <CardBody className="px-lg-5 py-lg-5">
            <div className="text-center text-muted mb-4">
              <small>Sign up with credentials</small>
            </div>
            <Form role="form" onSubmit={handleSubmit}>
              <FormGroup>
                <InputGroup className="input-group-alternative mb-3">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-hat-3" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input placeholder="Name" type="text" />
                </InputGroup>
              </FormGroup>
              <FormGroup>
                <InputGroup className="input-group-alternative mb-3">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                      <i className="ni ni-email-83" />
                    </InputGroupText>
                  </InputGroupAddon>
                  <Input placeholder="Email" type="email" autoComplete="new-email" />
                </InputGroup>
              </FormGroup>

              {/* PDF File Input */}
              <FormGroup>
                <InputGroup className="input-group-alternative mb-3">
                  <Input type="file" onChange={handleFileChange} accept="application/pdf" />
                </InputGroup>
              </FormGroup>

              {/* Submit Button */}
              <div className="text-center">
                <Button className="mt-4" color="primary" type="submit">
                  Submit
                </Button>
              </div>
            </Form>

            {/* Display Alphanumeric Code */}
            {code && (
              <div className="text-center mt-3">
                <strong>Your Code:</strong> {code}
              </div>
            )}
          </CardBody>
        </Card>
      </Col>
    </>
  );
};

export default Register;
